const cartProductsReducer = (state = [], action) => {
  switch (action.type) {
    case "ADD":
      return [...state, action.newProduct];

    case "DEL":
      const deleted = state.filter((product) => product.name !== action.name);
      return deleted;

    default:
      return state;
  }
};

export default cartProductsReducer;
