import "./styles.css";

const Home = ({ products }) => {
  const handleAdd = () => {};

  products.map((product) => {
    <div>
      <img src={product.img} alt="Imagem do produto"></img>
      <h4>{product.name}</h4>
      <p>{product.value}</p>
      <button type="button" onClick={handleAdd}></button>
    </div>;
  });
};

export default Home;
