import "./styles.css";

const Home = ({ products }) => {
  const handleAdd = () => {};

  return (
    <>
      {products.map((product) => {
        <div>
          console.log({product.img})
          <img src={product.img} alt="Imagem do produto"></img>
          <h4>{product.name}</h4>
          <p>{product.value}</p>
          <button type="button" onClick={handleAdd}></button>
        </div>;
      })}
    </>
  );
};

export default Home;
